package eclipsepracticepackage.java;

public class Switchcondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//first we need to create the string what the result you want
		String day= "day6";
		//this is the switch statement inside switch statement we need to give the string reference variable
		switch(day) {
		//based on the case result will display
		case "day1":
			System.out.println("Monday");
			break;
			
		case "day2":
			System.out.println("Tuesday");
			break;
			
		case "day3":
			System.out.println("Wednesday");
			break;
			
		case "day4":
			System.out.println("Thursday");
			break;
			
		case "day5":
			System.out.println("Friday");
			break;
			
		case "day6":
			System.out.println("Saturday");
			break;
			
		case "day7":
			System.out.println("Sunday");
			break;
			
		default:
			System.out.println("Invalid");
			
		
		
		
		
		
		
		}
		
		
		
		
		
		
		
		
	}

}
